INSERT INTO observations (duration, location, witnesses, colors, notes, latitude, longitude, datetime) VALUES (3600.0, '2660 Brøndby Strand', 4.0, 'orange, hvid, grå', 'Vi så også de „såkaldte balloner“ kl 22-23.
INSERT INTO observations (duration, location, witnesses, colors, notes, latitude, longitude, datetime) VALUES (600.0, '3230 Udsholt Strand', 4.0, 'hvid, sort', 'Vi er 4 personer, der sidder og nyder den lyse aften- og nattehimlen, da vi observerer et skarpt hvidt lys, der kommer mod os oppe på himlen fra vest lidt ude over havet. Vi antager selvfølgelig først, at det er et fly, men undrer os samtidig over, hvor det skal fra og til.
INSERT INTO observations (duration, location, witnesses, colors, notes, latitude, longitude, datetime) VALUES (30.0, 'Over Ørslev4760 Vordingborg', 3.0, 'NULL', 'http://www.sufoi.dk/info/balloner.php', 55.005563, 11.907797, '2011-06-13 23:00:00');
