INSERT INTO observations (duration, location, witnesses, colors, notes, latitude, longitude, datetime) VALUES (180.0, '9490 Pandrup', 2.0, 'ingen angivet farve', '2 stillestående kraftige lys.', 57.209427, 9.67901, '2011-07-26 21:49:00');
INSERT INTO observations (duration, location, witnesses, colors, notes, latitude, longitude, datetime) VALUES (20.0, '4030 Tune', 2.0, 'guld', 'Min kæreste hentede mig fra job. Pludseligt på vej hjem ser jeg 6 underlige prikker nærmest guld-farvede oppe på himmelen. Jeg blev så chokeret, så jeg bare råbte„se“.
INSERT INTO observations (duration, location, witnesses, colors, notes, latitude, longitude, datetime) VALUES (60.0, '2750 Ballerup', 1.0, 'orange-rød', 'Var ude i min fars firmabygning i Ballerup, og jeg beslutter mig for lige at gå en tur med min hund Anton, inden jeg kørte hjem af. Det er noget, jeg har gjort ofte, da der ligger en stor golfbane lige ved siden af som er helt rolig om aftenen.
