INSERT INTO observations (duration, location, witnesses, colors, notes, latitude, longitude, datetime) VALUES (120.0, '7300 Jelling', 2.0, 'orangegul', 'Fra min sofa så jeg pludselig en gulorange lysplet på størrelse med en appelsin komme frem bag et træ i vestlig retning og bevæge sig på himlen.
INSERT INTO observations (duration, location, witnesses, colors, notes, latitude, longitude, datetime) VALUES (60.0, '9700 Brønderslev', 1.0, 'rød', 'Ufoen bevægede sig ligesom en flyvemaskine med røde lys, på et jævn niveau. Der var også lidt af en orange farve i lyset. Det kunne måske har været en varmluftballon eller noget lignende.', 57.270013, 9.944863, '2011-07-31 00:20:00');
