INSERT INTO observations (duration, location, witnesses, colors, notes, latitude, longitude, datetime) VALUES (7200.0, '8370 Hadsten', 5.0, 'NULL', 'Underlige farvede prikker, der blinker, men flytter sig ikke. Langt nede på himlen.', 56.329089, 10.052386, '2012-02-15 18:30:00');
