INSERT INTO observations (duration, location, witnesses, colors, notes, latitude, longitude, datetime) VALUES (600.0, '3200 Helsinge', 20.0, 'lysende', 'Kigger tilfældigt op i himmelen og ser en lysende kugle flyve stærkt fremad, efter få minuter kommer der en mere.', 56.021107, 12.195109, '2012-06-23 23:43:00');
INSERT INTO observations (duration, location, witnesses, colors, notes, latitude, longitude, datetime) VALUES (10.0, '4293 Dianalund', 6.0, 'sort', 'Vi var til en lille grillfest, og spillede kroket i haven hele aftenen. Jeg kan ikke huske, hvordan vi egentlig fik øje på den, men en sagde„Hvad fanden er dét?“og pegede op mod himlen.
