INSERT INTO observations (duration, location, witnesses, colors, notes, latitude, longitude, datetime) VALUES (60.0, '7000 Fredericia', 1.0, 'rødlig orange', 'En lysende (i orange røde og lysende skær) skikkelse passerede stille på himlen totalt uden lyd.', 55.564906, 9.743651, '2012-07-14 22:50:00');
INSERT INTO observations (duration, location, witnesses, colors, notes, latitude, longitude, datetime) VALUES (120.0, '5892 Gudbjerg', 1.0, 'lys metallisk', 'Jeg var lige kommet hjem fra arbejde og havde dagen forinden lovet min kone at klippe vores hække. Jeg parkerede bilen, og gik ud til hækken for at se på den, inden jeg gik i gang. Da jeg bor på landet, stod jeg i vores indkørsel, og kiggede ud over naboens marker. Jeg tænkte, hvor gyldent og flot det hele var, et sand fryd for øjet, og fortsat med at kigge på skyerne og det flotte blå himmel.
INSERT INTO observations (duration, location, witnesses, colors, notes, latitude, longitude, datetime) VALUES (240.0, '2610 Rødovre', 1.0, 'orange, rød', 'Fra tagvindue ser jeg ud mod København 3 orange/røde lysende objekter, som bevæger sig fra syd til nord på mørk himmel. Med en 7x50 kikkert er det muligt at se, at lysene ikke kommer fra en nødraket, idet der er et tydeligt korpus. Det er svært at afgøre afstanden, som kan være nogle km.
INSERT INTO observations (duration, location, witnesses, colors, notes, latitude, longitude, datetime) VALUES (360.0, '9900 Frederikshavn', 1.0, 'metallisk', 'Det var en stille og rolig, morgen da jeg gik mig en tur ned mod byen. Da jeg kom til Emilievej 56, så jeg et flyvene metalisk objekt i vestlig retning.', 57.441213, 10.533622, '2012-07-17 05:01:00');
