INSERT INTO observations (duration, location, witnesses, colors, notes, latitude, longitude, datetime) VALUES (600.0, '4300 Holbæk', 1.0, 'hvid', 'Så noget skarpt lys, troede det var landingslys på et fly. Men kunne se, at det ikke var det. Det var lydløst, og virkede som kontroleret fart. Efter et øjeblik, kunne jeg hører en lyd som en fuserraket. Der kom lidt stjerner fra bunden.', 55.718612, 11.713547, '2012-07-26 23:45:00');
