INSERT INTO observations (duration, location, witnesses, colors, notes, latitude, longitude, datetime) VALUES (60.0, 'Havnen, 3250 Gilleje', 2.0, 'rødorange', 'Vi ligger i båden og ser op på himlen. Pludselig ser vi en rødorange kugle komme glidende med jævn fart hen over himlen. Den kommer fra vest og bevæger sig helt lydløst videre mod øst hvor den forsvinder.', 56.12682995, 12.3081565, '2012-03-08 22:47:00');
