INSERT INTO observations (duration, location, witnesses, colors, notes, latitude, longitude, datetime) VALUES (120.0, '6980 Tim', 1.0, 'hvid, gullig', 'http://planetariet.dk/artikel/ildkugle-set-fra-danmark-6', 56.196554, 8.30332, '2012-09-21 23:54:00');
