INSERT INTO observations (duration, location, witnesses, colors, notes, latitude, longitude, datetime) VALUES (20.0, '2630 Tåstrup', 2.0, 'hvid', 'Min veninde og jeg sidder ude på altanen sent om aftenen og ryger. Jeg sidder i retning af vest som den eneste og nyder en skyfri stjerneklar himmel.
