INSERT INTO observations (duration, location, witnesses, colors, notes, latitude, longitude, datetime) VALUES (600.0, 'Houstrup6830 Nørre Nebel', 1.0, 'rød', '2 røde kugleformede objecter kommer lydløs hen over himlen, i forholdsvis lav højde. Efter ca. 10 min kommer yderligere 2 og forsvinder ud over havet.', 55.773347, 8.304661, '2013-07-23 23:05:00');
