INSERT INTO observations (duration, location, witnesses, colors, notes, latitude, longitude, datetime) VALUES (20.0, '9700 Brønderslev', 1.0, 'rød', 'Så en rimmelig stor rød kugle komme fra syd-vest og så forsvinder så i nord øslig retning, 40 grader ca. Farten var rimmelig hurtig. Kl var 22/15 Det var en mærkelig oplevelse. Har aldrig set det før.', 57.270013, 9.944863, '2013-01-08 22:15:00');
