INSERT INTO observations (duration, location, witnesses, colors, notes, latitude, longitude, datetime) VALUES (7.0, '8000 Aarhus Havn', 1.0, 'hvid', 'Fiskede i Århus havn, da et jagerfly flyver lavt over havnen Kigger op i luften og ser et hvidt opjekt, 3 små kugler samlet.
