INSERT INTO observations (duration, location, witnesses, colors, notes, latitude, longitude, datetime) VALUES (60.0, '4440 Mørkøv', 2.0, 'sort', 'I bussen på vej hjem fra arbejde fik jeg øje på et sort objekt, der fløj i umidlbart i højde med, eller lidt under, skyerne.
