INSERT INTO observations (duration, location, witnesses, colors, notes, latitude, longitude, datetime) VALUES (180.0, 'Gundsømagle4040 Jyllinge', 2.0, 'sort', 'The object was black, without visible wings and as wide at both ends. It moved very slowly towards/over Roskilde, lower than a usual aircraft. We were driving in a car and could have watched it forever, if we had time. The picture looks rather bad and is not as representative.', 55.690348, 12.478085, '2013-08-29 18:30:00');
