INSERT INTO observations (duration, location, witnesses, colors, notes, latitude, longitude, datetime) VALUES (180.0, 'Ølsemagle, 4600 Køge', 2.0, 'metal, mørk grå', 'Det er onsdag morgen i Ølsemagle (Køge). Solen skinner fra en krystalklar skyfri himmel, og der er næsten vindstille. Jeg lufter hund og følges med min datter, som er på vej til skole.
INSERT INTO observations (duration, location, witnesses, colors, notes, latitude, longitude, datetime) VALUES (120.0, '8660 Skanderborg', 1.0, 'rødt, hvidt', 'Jeg kom kørende på E45 i nordgående retning mellem Horsens og Skanderborg. Jeg fik øje på en stillestående blinkende genstand oppe i luften (på en position svarende til kl. ca 11.30 set fra førersædet i en bil - hvis det giver nogen mening?)
INSERT INTO observations (duration, location, witnesses, colors, notes, latitude, longitude, datetime) VALUES (2.0, 'Multebjerg, 3400 Hillerød', 1.0, 'gråblå', 'Jeg var i Gribskov med en kammerat fra fredag d. 21-3 til lørdag d. 22-3-2014. Vi står ved et shelter tæt på Muldebjerg radarstation, hvor vi skulle overnatte.
