INSERT INTO observations (duration, location, witnesses, colors, notes, latitude, longitude, datetime) VALUES (1800.0, 'Stranden4736 Karrebæksminde', 2.0, 'sort, meget mørk', 'Vi fik pludselig øje på en sort/meget mørk ting på himlen.
