INSERT INTO observations (duration, location, witnesses, colors, notes, latitude, longitude, datetime) VALUES (300.0, 'Kvong, 6800 Varde', 2.0, 'mørk', 'Jeg var på vej hjem efter at have besøgt en god ven. Klokken var omkring 01.30. Da ser jeg et meget skarpt lys komme ud fra en sky og 5 sek. efter kom der et til. Da bremsede jeg bilen og tog et billed af 5 lys hængende i 45 graders vinkel og var på vej ned.
INSERT INTO observations (duration, location, witnesses, colors, notes, latitude, longitude, datetime) VALUES (2700.0, '8920 Randers NV', 1.0, 'hvid, grøn, rød', 'Det lignede en stjerne, der fløj i cirkler, zig zaggede op og ned og fløj hen over himlen mod højre og tilbage mod venstre og igen i cirkler.... Den blev ved med at være på himlen, da jeg gav mig til at sove.', 56.459233, 9.965716, '2014-07-31 02:45:00');
INSERT INTO observations (duration, location, witnesses, colors, notes, latitude, longitude, datetime) VALUES (300.0, '3630 Jægerspris', 1.0, 'grå', 'Observerer noget på himmelen, der svæver over lygtepæle. Jeg skynder mig at tage 3 billeder med min mobiltelefon.', 55.850581, 11.991652, '2014-09-20 09:00:00');
