INSERT INTO observations (duration, location, witnesses, colors, notes, latitude, longitude, datetime) VALUES (1.0, 'Overby4583 Sjællands Odde', 1.0, 'ingen', 'Ser fra vindue en „Ufo“ meget hurtigt passere højt oppe (1 sek.). Går ud for at observere og ser en mere passere i samme højde, samme fart, samme tid, men øst-vest.', 55.9745245, 11.300145, '2014-11-25 21:40:00');
