INSERT INTO observations (duration, location, witnesses, colors, notes, latitude, longitude, datetime) VALUES (3.0, '3330 Gørløse', 1.0, 'mørkegrå', 'Står og kikker nod nord efter en flyver - ud af øjenkrogen til venstre (mod vest), ser jeg der kommer et lys - tror det er starten på et stjerneskud. Da jeg ser nærmere på den, ser jeg den er rund og mørk og samtidig lysende. Den ændrede hverken farve eller form og fløj i lige linie, med samme fart mod øst og forsvant.', 55.885239, 12.197972, '2015-09-03 20:23:00');
INSERT INTO observations (duration, location, witnesses, colors, notes, latitude, longitude, datetime) VALUES (180.0, 'Voervadsbro8660 Skanderborg', 3.0, 'hvid, gyldent', 'Jeg går ud og ser et kraftigt lys på himlen, ligner f.ex en billygte i natten i styrke.
