INSERT INTO observations (duration, location, witnesses, colors, notes, latitude, longitude, datetime) VALUES (10.0, '2400 København NV', 2.0, 'hvid', 'Min kæreste sider ude på altanen og ser en dråbeformed ting, der flyver fra øst mod vest. Den flyvende ting lyste stærkt Hvidt på hele dens flade, hun kaldte på mig, og jeg nåede selv at se det nogle sek. Fløj med normal hastighed forsvandt bag skyerne.', 55.708916, 12.530014, '2015-12-05 20:55:00');
