INSERT INTO observations (duration, location, witnesses, colors, notes, latitude, longitude, datetime) VALUES (30.0, '2830 Virum', 1.0, 'hvid', 'Objektet fløj fra nordøst mod sydvest, forholdsvis langsomt, dvs. mere langsomt end et fly, jeg hørte ingen lyd. Jeg lagde mærke til objektet, fordi det ikke lignede et fly, og hastigheden, der ikke kan have været særlig høj, måske 100-200 km/t, højden vil jeg anslå til 1000-2000 m. Objektet fortsatte i samme retning til, jeg ikke kunne se det længere.', 55.793959, 12.469471, '2015-01-06 21:26:00');
