INSERT INTO observations (duration, location, witnesses, colors, notes, latitude, longitude, datetime) VALUES (300.0, '5000 Odense C', 1.0, 'brunlig', 'Ser fly/fugleligende genstand flyve ud og ind af en sky, størrelsen som det dobbelt af en F16.... den laver skarpe manøvrer og uden lyd og jettrails.... vingeformem er lidt som på en ørn, men den er alt for stor og hurtig til det kan være en ørn og hastigheden er ligeledes for høj.... efter denne luftakrobatik sætter genstandene pludselig i bevægelse i sydvestlig rettning lige under skyerne med høj hastighed.... lidt hurtigere end et F16 fly.... genstanden virker brunlig og uden refleksioner fra solen.', 55.397357, 10.377802, '2015-07-14 14:10:00');
INSERT INTO observations (duration, location, witnesses, colors, notes, latitude, longitude, datetime) VALUES (1.0, '2730 Herlev', 1.0, 'sort', 'Torsdag d. 16-7-2015 kl. 21:07 stod jeg ude i haven og så på den flotte aftenhimmel.
INSERT INTO observations (duration, location, witnesses, colors, notes, latitude, longitude, datetime) VALUES (5.0, '2900 Hellerup', 2.0, 'hvid', 'Vi ligger i haven om natten og spotter tre lys i en trekant bevæge sig meget stabilt mod vest. Afstanden mellem lysene gør os sikre på, det hverken var et højtgående fly eller helikopter, og man kunne ingenting høre. Det så også ud til at være relativt højt oppe, altså i hvert fald over de nederste tynde skyer der.', 55.733666, 12.565541, '2015-07-17 02:00:00');
INSERT INTO observations (duration, location, witnesses, colors, notes, latitude, longitude, datetime) VALUES (120.0, '7171 Uldum', 2.0, 'hvid', 'Min datter og jeg er ude at gå med hunden, da vi pludselig ser et meget klart hvidt, nærmest led agtigt, sitrende lys på himlen. Vi kunne hurtigt se at det ikke var et fly. Det var alt for stort og lysende.
INSERT INTO observations (duration, location, witnesses, colors, notes, latitude, longitude, datetime) VALUES (240.0, '2791 Dragør', 2.0, 'orangegul', 'http://www.ufo.dk/info/balloner.php', 55.592487, 12.671995, '2015-07-18 22:30:00');
INSERT INTO observations (duration, location, witnesses, colors, notes, latitude, longitude, datetime) VALUES (4.0, '2600 Glostrup', 2.0, 'grøn', 'Stort, lysegrønt objekt med lys grøn/gul aura omkring sig. Dukkede op bag nogle træer i Vestskoven og forsvandt efter få sekunder i et glimt, men min mand kunne se det meget småt længere ude kort efter, at jeg missede det. Tæt ved var to fly. Et der fløj mod øst, og et der fløj mod nord.', 55.669248, 12.395952, '2015-07-26 22:50:00');
INSERT INTO observations (duration, location, witnesses, colors, notes, latitude, longitude, datetime) VALUES (10.0, '7300 Jelling', 1.0, 'grøn', 'Kører på arbejde, og da jeg ca. 300 m fra min bopæl kører ned ad en bakke med skov og eng på begge sider, ser jeg fra højre ser en rund, grøn kugle komme ret stærkt hen over vejen i god højde og mod venstre. Få sek. efter forsvinder den uden hverken at dale eller stige, bare puf væk.
INSERT INTO observations (duration, location, witnesses, colors, notes, latitude, longitude, datetime) VALUES (10.0, '2600 Glostrup', 1.0, 'blå, grøn', 'Stort, lysegrønt objekt med lys grøn/gul aura omkring sig. Dukkede op bag nogle træer i Vestskoven og forsvandt efter få sekunder i et glimt, men min mand kunne se det meget småt længere ude kort efter, at jeg missede det. Tæt ved var to fly. Et der fløj mod øst, og et der fløj mod nord.', 55.669248, 12.395952, '2015-07-26 22:55:00');
