INSERT INTO observations (duration, location, witnesses, colors, notes, latitude, longitude, datetime) VALUES (1500.0, '9520 Skørping', NULL, 'hvid, rød', 'Observerede i nat den 27. September kl. 03.45 på den sydlige himmel, et flyvende objekt, der havde et meget kraftig hvidt lys med blinkende rødt signallys. Fløj mod vest. Meget langt ude, så det igennem et scop med et okular på 5 cm i bredden! Det tog 24 minutter for objektet, at flyve „de 5 cm“!', 56.837237, 9.885951, '2015-09-27 03:45:00');
