INSERT INTO observations (duration, location, witnesses, colors, notes, latitude, longitude, datetime) VALUES (900.0, '9400 Nørresundby', 2.0, 'rød, grøn', 'Jeg stod på altanen og røg en smøg, da jeg fik øje på et konstant rødt lys på himlen, der var lidt større end de klareste stjerner.
