INSERT INTO observations (duration, location, witnesses, colors, notes, latitude, longitude, datetime) VALUES (NULL, '2650 Hvidovre', 1.0, 'orange', 'Around 22:15 i saw a light in the sky brighter than the rest of the Stars. The direction was southeast, no pulsing, no sound. It was a bit greenish and there was a din glow around it. When it was covered in clouds you could see the glow even better.', 55.644171, 12.477209, '2017-04-19 04:06:00');
