INSERT INTO observations (duration, location, witnesses, colors, notes, latitude, longitude, datetime) VALUES (30.0, '4773 Stensved', 2.0, 'hvid', 'Så et kraftigt hvidt, rund lys komme fra NV hurtigt over himmelen i en lige buet retning mod SØ. Lavere end stjernehimlen. Tænkte på om det var en satellit, men flytter den sig så hurtigt?', 54.99716, 12.024465, '2017-06-18 22:00:00');
