INSERT INTO observations (duration, location, witnesses, colors, notes, latitude, longitude, datetime) VALUES (60.0, '3100 Hornbæk', 1.0, 'rød', 'Sad på sofa med fuld udsyn mod vest gennem stor glas væg mod terrasse/have i mørk sommerhusområde.
INSERT INTO observations (duration, location, witnesses, colors, notes, latitude, longitude, datetime) VALUES (600.0, '4400 Kalundborg', 2.0, 'hvid', 'Et hvid lys „kugle“ - bevægede sig roligt over sort himmel. For stor og lavt flyvende til at være en satellit. Ingen blink, ingen lyd.
INSERT INTO observations (duration, location, witnesses, colors, notes, latitude, longitude, datetime) VALUES (20.0, '9480 Løkken', 1.0, 'metallisk', 'Jeg ligger på verandaen og ser en dokumentar på min tablet. Jeg vender blikket mod øst og ser et metallisk, ovalt objekt, der lydløst svæver i sydlig retning stille og roligt. Jeg skynder mig at komme op og stå, men når ikke at tage et billede før opjektet er væk.', 57.370486, 9.71125, '2017-10-08 15:25:00');
INSERT INTO observations (duration, location, witnesses, colors, notes, latitude, longitude, datetime) VALUES (21600.0, '8763 Rasm Mølle', 4.0, 'lys', 'Var på vej hjem efter, jeg har været ude og spille bold med mine venner, da vi ser noget underligt på himlen. Vi går tættere på og kigger op. Der kom lige pludseligt et stort lys, så lignede det, at det fløj væk, så forsvant det, men det blev ved med at komme samme sted, og nogen gange var der flere.
INSERT INTO observations (duration, location, witnesses, colors, notes, latitude, longitude, datetime) VALUES (300.0, '2900 Hellerup', 1.0, 'rød', 'Jeg har set 5 røde lys, der flyver som et minut fra hiNULLden fra NV til N. Rød farve og ingen lyd. Jeg har også filmet med min telefon.', 55.733666, 12.565541, '2017-12-08 23:30:00');
INSERT INTO observations (duration, location, witnesses, colors, notes, latitude, longitude, datetime) VALUES (2.0, '4550 Asnæs', 1.0, 'hvid, blå', 'I går den 14. omkring kl. 21 (det var stadig lyst) observer jeg (fra min sædvanlige plads i sofaen ) et stærkt hvidt/blåligt objekt på ca. ø 5 cm, bevæge sig i en vinkel på omkring 30 grader i forhold til lodret - nedadgående.
INSERT INTO observations (duration, location, witnesses, colors, notes, latitude, longitude, datetime) VALUES (10.0, '1878 Frederiksberg C', 2.0, 'gul', 'Så tre styk lys, der mindede om store stjerneskud. Gul/orange i farven og med en let gnistren. De faldt ned over himmelen synkront med hiNULLden og lavede over horisonten en skarp drejning, stadig synkront.', 55.676791, 12.544781, '2017-08-25 21:15:00');
INSERT INTO observations (duration, location, witnesses, colors, notes, latitude, longitude, datetime) VALUES (5.0, '2860 Søborg', 1.0, 'guld, gul', 'Alle de 3 lys fulgte efter hiNULLden.', 55.736468, 12.508873, '2017-08-25 21:24:00');
