INSERT INTO observations (duration, location, witnesses, colors, notes, latitude, longitude, datetime) VALUES (600.0, '7560 Hjerm', 1.0, 'hvid', 'Lysende klar, flyvende genstand, lydløs, langsom i forhold til fly eller helikopter.', 56.436895, 8.635462, '2019-10-07 03:10:00');
