INSERT INTO observations (duration, location, witnesses, colors, notes, latitude, longitude, datetime) VALUES (NULL, '7451 Sunds', 2.0, 'hvid, lys', 'De flyvende objekter minder om lysene små skyer eller lyskugler. De er runde og lidt aflange og blinker en gang i mellem. Deres flyve rutine cirkulerede meget omkring hiNULLden, med små hurtig bevægelser til enten højre eller venstre.
INSERT INTO observations (duration, location, witnesses, colors, notes, latitude, longitude, datetime) VALUES (20.0, '8950 Ørsted', 1.0, 'rød', 'Kom gående hjem og så 2 røde blinkende lys i det fjerne, som ligesom fløj nedad, efter 20 sekunder var de væk. Der var ingen røgsky eller andet på himlen efter.', 56.500781, 10.336375, '2020-01-13 16:57:00');
