INSERT INTO observations (duration, location, witnesses, colors, notes, latitude, longitude, datetime) VALUES (300.0, '5631 Ebberup', 2.0, 'lys', 'Det så ud som om, der kom et lys højere oppe fra end de stjerner, man kan se, lyset kom nærmere og blev til den største stjerne på himlen. Stjernen bevægede sig rundt i en meget lille omkreds, hvorefter den forsvandt op og blev til en næsten usynlig prik efter ca. 5 min.', 55.242109, 9.973687, '2020-02-19 21:00:00');
