INSERT INTO observations (duration, location, witnesses, colors, notes, latitude, longitude, datetime) VALUES (3.0, '5000 Odense C', 1.0, 'hvid', 'Jeg ser 3 hvide/lysende punkter på en linje flyve meget hurtigt. Der er ingen blink, og det er ikke stjerneskud, som jeg kender det.', 55.397357, 10.377802, '2020-04-05 01:30:00');
