INSERT INTO observations (duration, location, witnesses, colors, notes, latitude, longitude, datetime) VALUES (60.0, '5935 Bagenkop', 1.0, 'NULL', 'Jeg kommer kørende - det går en smule op ad bakke, og der er 2 skarpe sving. Over mig ud for bilens forrude ser jeg, at der er et fly på vej.
INSERT INTO observations (duration, location, witnesses, colors, notes, latitude, longitude, datetime) VALUES (10.0, '9560 Hadsund', 1.0, 'ildrød, mørkerød', 'Så i nat, det der muligvis kan betegnes som en asteroide brænde op i atmosfæren.', 56.719352, 10.115111, '2020-07-06 00:10:00');
INSERT INTO observations (duration, location, witnesses, colors, notes, latitude, longitude, datetime) VALUES (60.0, '2730 Herlev', 1.0, 'ild, rød', 'Oplevede et underligt lys på himlet. Den bevægede sig i forskellige retninger på himlen, var meget klar i en varm, nærmest glødende, farve. Pludselig forsvandt det. Har billeder.', 55.724817, 12.437722, '2020-06-23 23:14:00');
INSERT INTO observations (duration, location, witnesses, colors, notes, latitude, longitude, datetime) VALUES (900.0, '8800 Viborg', 1.0, 'rød', 'Kigger ud af mit vindue og ser så denne meget røde cirkel, og det er ikke Venus eller Mars, for det var cirklen for stor til, og den bevægede sig fra side til side, og lyset blev svager og skarpere, og det forsvandt meget hurtigt i øst.', 56.446913, 9.405935, '2020-06-28 02:45:00');
INSERT INTO observations (duration, location, witnesses, colors, notes, latitude, longitude, datetime) VALUES (15.0, '8883 Gjern', 2.0, 'hvid, grøn', 'Mig og en kammerat er taget i skoven på en bakketop, der er herude, hvor vi bor, for at se på en meteorregn.
