INSERT INTO observations (duration, location, witnesses, colors, notes, latitude, longitude, datetime) VALUES (7.0, '2700 Brønshøj', NULL, 'hvid', 'Vores kamera opfangede to lysende objekter, som i mine øjne er uforklarligt.
