INSERT INTO observations (duration, location, witnesses, colors, notes, latitude, longitude, datetime) VALUES (900.0, '9220 Aalborg Øst', 3.0, 'blå', 'Min kæreste, vores fælles ven og jeg så her i nat ca. 04.50-05.05 to flyvende blå stjerne lignende objekter, der fløj i mønstre, man ikke har set fra fly eller lignende og i ekstrem høj fart taget i betragtning hvor langt væk de var. Vi så alle tre dette!', 57.04426285, 9.9756421, '2023-03-26 05:00:00');
